import * as CryptoJS from 'crypto-js';

export class User {
    constructor(private _user: string, private _userKey: string) {}

    get user() {
        return this._user;
    }

    get userKey() {
        return this._userKey;
    }

    getSignature(stime: string) {
        const hash = CryptoJS.SHA1(this.user+this.userKey+stime);
        return CryptoJS.enc.Hex.stringify(hash);
    }
};